CREATE TABLE `pai_db`.`user` ( `User_ID` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Password` VARCHAR(255) NOT NULL , `Alias` VARCHAR(6) NOT NULL , `Country` VARCHAR(255) NULL DEFAULT NULL , `Register` DATE NOT NULL , PRIMARY KEY (`User_ID`), UNIQUE (`Name`), UNIQUE (`Alias`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`fields` ( `Field_ID` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Alias` VARCHAR(4) NOT NULL , PRIMARY KEY (`Field_ID`), UNIQUE (`Name`), UNIQUE (`Alias`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`organisation` ( `Ogr_ID` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Leader` BIGINT UNSIGNED NOT NULL , `Description` TEXT NULL DEFAULT NULL , `Private` BOOLEAN NOT NULL DEFAULT FALSE , `Register` DATE NOT NULL , PRIMARY KEY (`Ogr_ID`), UNIQUE (`Name`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`projects` ( `Proj_ID` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Org_ID` BIGINT UNSIGNED NOT NULL , `Proj_Leader_ID` BIGINT UNSIGNED NOT NULL , `Description` VARCHAR(5000) NULL , `Private` BOOLEAN NOT NULL DEFAULT FALSE , `Date_create` DATE NOT NULL , PRIMARY KEY (`Proj_ID`), UNIQUE (`Name`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`charts` ( `Chart_ID` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Project_ID` BIGINT UNSIGNED NOT NULL , `Chart_Leader` BIGINT UNSIGNED NOT NULL , `Description` VARCHAR(5000) NULL DEFAULT NULL , `Private` BOOLEAN NOT NULL DEFAULT FALSE , PRIMARY KEY (`Chart_ID`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`columns` ( `Col_Id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Chart` BIGINT UNSIGNED NOT NULL , `Archive` BOOLEAN NOT NULL DEFAULT FALSE , `Position` BIGINT UNSIGNED NOT NULL , PRIMARY KEY (`Col_Id`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`notes` ( `Note_ID` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT , `Title_note` VARCHAR(50) NOT NULL , `Col_ID` BIGINT UNSIGNED NOT NULL , `Field_ID` BIGINT UNSIGNED NOT NULL , `Color` VARCHAR(6) NOT NULL DEFAULT '5e7cb8' COMMENT '[HEX] color representation' , `Description` VARCHAR(5000) NULL , `Owner` BIGINT UNSIGNED NOT NULL , `Date_Create` DATE NOT NULL , `Date_End` DATE NULL , `Urgent` BOOLEAN NOT NULL DEFAULT FALSE , `Done` BOOLEAN NOT NULL DEFAULT FALSE , `Archive` BOOLEAN NOT NULL DEFAULT FALSE , PRIMARY KEY (`Note_ID`)) ENGINE = InnoDB; 

CREATE TABLE `pai_db`.`mid1 fi_us` ( `ID` SERIAL NOT NULL AUTO_INCREMENT , `User` BIGINT UNSIGNED NOT NULL , `Field` BIGINT UNSIGNED NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`mid2 or-us` ( `ID` SERIAL NOT NULL AUTO_INCREMENT , `Organisation` BIGINT UNSIGNED NOT NULL , `User` BIGINT UNSIGNED NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`mid3 pro-us` ( `ID` SERIAL NOT NULL AUTO_INCREMENT , `Project` BIGINT UNSIGNED NOT NULL , `User` BIGINT UNSIGNED NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`mid4 ch-us` ( `ID` SERIAL NOT NULL AUTO_INCREMENT , `Chart` BIGINT UNSIGNED NOT NULL , `User` BIGINT UNSIGNED NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`mid5 no-us` ( `ID` SERIAL NOT NULL AUTO_INCREMENT , `Note` BIGINT UNSIGNED NOT NULL , `User` BIGINT UNSIGNED NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB;

