CREATE TABLE `pai_db`.`user` ( `User_ID` BIGINT NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Password` VARCHAR(255) NOT NULL , `Alias` TEXT NOT NULL , `Country` VARCHAR(255) NULL DEFAULT NULL , `Register` DATE NOT NULL , PRIMARY KEY (`User_ID`), UNIQUE (`Name`), UNIQUE (`Alias`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`fields` ( `Field_ID` INT NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Alias` VARCHAR(4) NOT NULL , PRIMARY KEY (`Field_ID`), UNIQUE (`Name`), UNIQUE (`Alias`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`organisation` ( `Ogr_ID` BIGINT NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Leader` INT NOT NULL , `Description` TEXT NULL DEFAULT NULL , `Private` BOOLEAN NOT NULL DEFAULT FALSE , `Register` DATE NOT NULL , PRIMARY KEY (`Ogr_ID`), UNIQUE (`Name`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`projects` ( `Proj_ID` BIGINT NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Org_ID` INT NOT NULL , `Proj_Leader_ID` INT NOT NULL , `Description` VARCHAR(5000) NULL , `Private` BOOLEAN NOT NULL DEFAULT FALSE , `Date_create` DATE NOT NULL , PRIMARY KEY (`Proj_ID`), UNIQUE (`Name`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`charts` ( `Chart_ID` BIGINT NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Project_ID` BIGINT NOT NULL , `Chart_Leader` BIGINT NOT NULL , `Description` VARCHAR(5000) NULL DEFAULT NULL , `Private` BOOLEAN NOT NULL DEFAULT FALSE , PRIMARY KEY (`Chart_ID`)) ENGINE = InnoDB; 
CREATE TABLE `pai_db`.`columns` ( `Col_Id` BIGINT NOT NULL AUTO_INCREMENT , `Name` VARCHAR(255) NOT NULL , `Chart` BIGINT NOT NULL , `Archive` BOOLEAN NOT NULL DEFAULT FALSE , `Position` INT NOT NULL , PRIMARY KEY (`Col_Id`)) ENGINE = InnoDB; 
